package sameornot;

public class OddorEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int Number1 = 75 ;
      
       
       if(Number1%2==0){
    	  System.out.println("This is an Even Number");
    	  }
       else {
    	   System.out.println("This is an odd Number");
 	      }
    	   
       
	}

}
